 = data.code;                        var msg  = data.msg;                        switch(code){                            case 1:                                layer.alert(msg, {icon: 6,shade: false}, function(index){                                    parent.location.reload();                                });                                break;                            default:                                layer.alert(msg, {icon: 5});                        }                    }                });            });            function  ajaxFile() {                var HTTP_PATH = "<?php echo $HTTP_PATH?>";                var index = layer.msg("正在上传请稍等....",{time:1000000});                var uploadUrl = 'sale_do.php?act=upload';//处理上传的图片                $.ajaxFileUpload({                    url           : uploadUrl,                    fileElementId : 'file',                    dataType      : 'json',                    success       : function(data, status){                        var code = data.code;                        var msg  = data.msg;                        layer.close(index);                        switch(code){                            case 1:                                $("#val1").attr("src",HTTP_PATH+msg);                                $("#file_url").val(msg);                                break;                            default:                                layer.alert(msg, {icon: 5,closeBtn: 0});                        }                    },                    error: function (data, status, e){                        layer.alert(e);                    }                });                $('input[type="file"]').change(function(e) {                    ajaxFile(this);                });            };            $('input[type="file"]').change(function(e) {                ajaxFile(this);            });        });    </script></head><body><div id="formlist">    <p>        <label>内容简介：</label>        <input type="text" class="text-input input-length-30" name="title" id="title" value="<?php echo $info['title'];?>" placeholder="建议不超过30字"/><!--        <span class="warn-inline">* 不得超过10个字</span>-->    </p>    <p>        <label>图片上传</label>        <input id="file_url"  name="file_url" type="hidden"  value="<?php echo $info['picture'];?>" />        <input id="file" class="upfile_btn" type="file" name="file" style="height:24px;"/>        <span class="warn-inline">*</span>    </p>    <p  style="text-align:center;color:red;">建议上传图片尺寸：80px*80px</p>    <p style="padding-left:150px;"><img id="val1" src="<?php echo $HTTP_PATH.$info['picture'];?>" width="80px" height="80px" alt="" /></p>    <p>        <label style="width: 140px;">二维码简介：</label>        <input type="text" class="text-input input-length-30" name="content" id="content" value="<?php echo $info['content'];?>"/>    </p><!--    <p  style="text-align:center;color:red;">* 不得超过80个字</p>-->    <p>        <label></label>        <input type="submit" id="btn_submit" class="btn_submit" value="保 存" style="display:block;margin:0 auto"/>    </p></div></body></html>