<?php/** * Created by PhpStorm. * User: ftx * Date: 2019/6/6 * Time: 11:20 AM */try {    $roomId = isset($_POST['id'])?safeCheck($_POST['id']):'0';    if(empty($roomId)) throw new MyException("缺少群id参数",101);    $roomInfo = Chat_room::getInfoById($roomId);    $roomInfo['last_msg_addtime'] = getDateStr($roomInfo['last_msg_addtime']);    $roomInfo['addtime'] = getDateStr($roomInfo['addtime']);    echo action_msg_data(API::SUCCESS_MSG, API::SUCCESS, $roomInfo);}catch (MyException $e){    $api->ApiError($e->getCode(), $e->getMessage());}